    return [
      item.template.code,
      item.template.title.substring(0, 40) + (item.template.title.length > 40 ? '...' : ''),
      '', // EMPTY - prevent default text rendering
      item.measuredValue || '-',
      item.isMandatory ? 'Yes' : 'No',
      item.score ? item.score.toString() : '-',
