      3: { cellWidth: 30, font: 'helvetica' }, // Value
      4: { cellWidth: 20, font: 'helvetica' }, // Mandatory
      5: { cellWidth: 15, font: 'helvetica' }, // Score
    },
    didDrawCell: (data) => {
      // ONLY render colored text in status column (column 2)
