import { db } from '@/lib/db'
import DashboardClient from '@/components/dashboard/DashboardClient'

// Force dynamic rendering since we are fetching data that changes
export const dynamic = 'force-dynamic'

async function getProjects() {
  try {
    const projects = await db.project.findMany({
      include: {
        inspections: {
          include: {
            phase: true,
            items: {
              include: {
                template: true
              }
            }
          }
        },
        _count: {
          select: {
            inspections: true
          }
        }
      },
      orderBy: {
        createdAt: 'desc'
      }
    })

    // Get all phases from database
    const allPhases = await db.phase.findMany({
      orderBy: { order: 'asc' }
    })

    // Calculate project statistics
    const projectsWithStats = projects.map(project => {
      const phaseNames = allPhases.map(p => p.name)
      const phaseStats = phaseNames.map(phaseName => {
        const phaseInspections = project.inspections.filter(i => i.phase.name === phaseName)
        const latestInspection = phaseInspections.sort((a, b) =>
          new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
        )[0]

        const phase = allPhases.find(p => p.name === phaseName)

        return {
          id: phase?.id || '',
          name: phaseName,
          score: latestInspection?.score || null,
          status: latestInspection?.status || 'NOT_STARTED',
          order: phase?.order || 0,
          inspectionId: latestInspection?.id
        }
      })

      const completedPhases = phaseStats.filter(p => p.status === 'APPROVED').length
      const overallProgress = Math.round((completedPhases / phaseStats.length) * 100)

      const scores = phaseStats.filter(p => p.score !== null).map(p => p.score!)
      const averageScore = scores.length > 0
        ? Math.round(scores.reduce((a, b) => a + b, 0) / scores.length * 10) / 10
        : 0

      const activeIssues = project.inspections
        .filter(i => i.status === 'SUBMITTED' || i.status === 'NEEDS_REWORK')
        .reduce((acc, inspection) => {
          const notOkItems = inspection.items.filter(item => item.status === 'NOT_OK' && item.template.isMandatory)
          return acc + notOkItems.length
        }, 0)

      return {
        ...project,
        overallProgress,
        averageScore,
        activeIssues,
        phases: phaseStats
      }
    })

    return projectsWithStats
  } catch (error) {
    console.error('Error fetching projects:', error)
    return []
  }
}

async function getCriticalIssues(projectId?: string) {
  try {
    // Get all inspections for the project (or all if no projectId specified)
    // If no project ID is provided, we fetch for the first project usually, 
    // but here we might want to fetch for all or handle it in the client.
    // For initial load, let's fetch for the most recent project if available, or all.
    // To match previous behavior, we'll fetch all if no ID, but client filters by selected project.
    // Actually, the client logic was: select first project -> fetch issues for it.
    // So here we should probably fetch issues for the first project if we can determine it,
    // OR fetch all and let client filter (might be heavy),
    // OR just pass empty and let client fetch (defeats the purpose).
    // Let's try to fetch for the first project if we can.

    // We'll rely on the projects fetch to determine the first project ID if needed, 
    // but we can't easily share state between these two async functions without calling one inside the other 
    // or doing it sequentially in the component.

    // Let's just fetch ALL critical issues for now, assuming the dataset isn't huge yet.
    // Optimization: We could fetch only for the most recent project in the Page component.

    const inspections = await db.qCInspection.findMany({
      where: projectId ? { projectId } : {},
      include: {
        project: {
          select: {
            id: true,
            name: true,
            code: true
          }
        },
        phase: {
          select: {
            id: true,
            name: true
          }
        },
        items: {
          include: {
            template: {
              select: {
                code: true,
                title: true,
                isMandatory: true
              }
            }
          }
        }
      }
    })

    // Collect all critical items (mandatory items with NOT_OK status)
    const criticalItems: Array<{
      id: string
      inspectionId: string
      phaseId: string
      phase: string
      item: string
      issue: string
      code: string
      projectId: string
      projectName: string
      projectCode: string
    }> = []

    inspections.forEach(inspection => {
      inspection.items.forEach(item => {
        if (item.template.isMandatory && item.status === 'NOT_OK') {
          criticalItems.push({
            id: item.id,
            inspectionId: inspection.id,
            phaseId: inspection.phase.id,
            phase: inspection.phase.name,
            item: item.template.title,
            issue: item.notes || 'Item wajib tidak lulus pemeriksaan',
            code: item.template.code,
            projectId: inspection.project.id,
            projectName: inspection.project.name,
            projectCode: inspection.project.code
          })
        }
      })
    })

    return criticalItems
  } catch (error) {
    console.error('Error fetching critical issues:', error)
    return []
  }
}

export default async function Home() {
  const projects = await getProjects()

  // Optimization: Only fetch critical issues for the first project initially
  // to match the default selected project behavior
  let criticalIssues: any[] = []
  if (projects.length > 0) {
    criticalIssues = await getCriticalIssues(projects[0].id)
  }

  return (
    <DashboardClient
      initialProjects={projects}
      initialCriticalIssues={criticalIssues}
    />
  )
}




